mapper.select:
select board_id, user_id, title, content, created_at, updated_at
from board
where board_id=#{boardId}

select user_id
from board
where board_id=#{boardId}

select board_id, title, updated_at
from board
where user_id=#{userId}

mapper.update:
update board
set title=#{title}, content=#{content}, updated_at=#{updatedAt}
where board_id=#{boardId}
